import React, { useState, useEffect } from "react";
import ItemModal from "./ItemModal";
import { ClipLoader } from "react-spinners";
import useProductStore from "../Data/AsyncMock";
import useCartStore from '../../Store/CartStore';


const Shop = () => {
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedCategory, setSelectedCategory] = useState("Todos");

  const products = useProductStore((state) => state.products);
  const addToCart = useCartStore((state) => state.addToCart);
  const cart = useCartStore((state) => state.cart);
  const removeFromCart = useCartStore((state) => state.removeFromCart);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 1000);
    return () => clearTimeout(timer);
  }, []);

  const handleBuyClick = (product) => {
    if (product.stock > 0) {
      addToCart(product, 1); // Añadir un producto al carrito
    }
  };

  return (
    <div className="max-w-[1240px] mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-[#e6e8eb] text-center mb-8">Tienda</h1>

      {/* Mostrar carrito */}
      <div className="mb-8 p-4 bg-[#20232a] rounded-lg">
        <h2 className="text-2xl font-bold text-white mb-4">Carrito</h2>
        {cart.length === 0 ? (
          <p className="text-[#aebbc9]">El carrito está vacío.</p>
        ) : (
          <ul>
            {cart.map((item) => (
              <li key={item.id} className="flex justify-between items-center mb-2">
                <span>{item.name} (x{item.quantity})</span>
                <button
                  onClick={() => removeFromCart(item.id, item.quantity)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  Eliminar
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Mostrar productos */}
      {/* ... El código de productos sigue igual */}
    </div>
  );
};

export default Shop;
