import { create } from "zustand";

const useProductStore = create((set) => ({
  products: [
    { id: 1, name: "Producto 1", price: "$10", stock: 10, category: "Minimalista", img: 'https://i.pinimg.com/550x/39/37/b8/3937b8f1e23316af9039ff95155856de.jpg', desc: "Con un diseño limpio y sin adornos innecesarios..." },
    { id: 2, name: "Producto 2", price: "$20", stock: 5, category: "Gamer", img: 'https://www.aguilamuebles.cl/tienda/wp-content/uploads/2021/08/S1932-2-1-600x600.png', desc: "El escritorio cuenta con un sistema de gestión de cables..." },
    { id: 3, name: "Producto 3", price: "$30", stock: 8, category: "Contemporáneo", img: 'https://imcesa.com.pe/wp-content/uploads/2023/09/muebles-en-lima-de-sala-1-scaled.webp', desc: "Este encantador sillón turquesa es una pieza central..." },
    { id: 4, name: "Producto 4", price: "$40", stock: 3, category: "Gamer", img: 'https://pegasum.cl/wp-content/uploads/2022/02/cru-nr2-600x900.jpg', desc: "Fabricada con materiales de alta calidad..." },
    { id: 5, name: "Producto 5", price: "$50", stock: 12, category: "Moderno", img: 'https://interimobel.mx/cdn/shop/products/ARMARIOCHILE.png?v=1650485010&width=2048', desc: "Este elegante armario está diseñado para aportar funcionalidad..." },
  ],

  // Reducir stock de un producto
  reduceStock: (productId, quantity) =>
    set((state) => ({
      products: state.products.map((product) =>
        product.id === productId
          ? { ...product, stock: product.stock - quantity }
          : product
      ),
    })),
}));

export default useProductStore;
