import { create } from 'zustand';
import mueble1 from "../../images/mueble1.png";
import mueble2 from "../../images/mueble2.png";
import mueble3 from "../../images/mueble3.png";
import mueble4 from "../../images/mueble4.png";
import mueble5 from "../../images/mueble5.webp"; 



const useProductStore = create((set) => ({
  products: [
    { id: 1, name: "Producto 1", price: "$10", img: mueble1, stock: 10, category: "Minimalista" },
    { id: 2, name: "Producto 2", price: "$20", img: mueble2, stock: 5, category: "Gamer" },
    { id: 3, name: "Producto 3", price: "$30", img: mueble3, stock: 8, category: "Contemporáneo" },
    { id: 4, name: "Producto 4", price: "$40", img: mueble4, stock: 3, category: "Gamer" },
    { id: 5, name: "Producto 5", price: "$50", img: mueble5, stock: 12, category: "Moderno" },
  ],
  reduceStock: (id, quantity = 1) =>
    set((state) => ({
      products: state.products.map((product) =>
        product.id === id
          ? { ...product, stock: Math.max(0, product.stock - quantity) } // Evitar stock negativo
          : product
      ),
    })),
  restoreStock: (id, quantity) =>
    set((state) => ({
      products: state.products.map((product) =>
        product.id === id ? { ...product, stock: product.stock + quantity } : product
      ),
    })),
}));

export default useProductStore;
