import React, { useState } from "react";

const ItemModal = ({ product, onClose, onAddToCart }) => {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    if (quantity <= product.stock) {
      onAddToCart(product, quantity);
    } else {
      alert("Cantidad supera el stock disponible.");
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 px-4">
      <div className="bg-[#0b0d0f] p-6 rounded-lg max-w-[800px] w-full">
        <img src={product.img} alt={product.name} className="w-full h-64 object-cover rounded-lg mb-4" />
        <h2 className="text-3xl font-bold mb-4">{product.name}</h2>
        <p className="text-xl mb-4">Precio: {product.price}</p>
        <p className="text-xl mb-4">Stock disponible: {product.stock}</p>

        <div className="flex items-center mb-6">
          <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>-</button>
          <span>{quantity}</span>
          <button onClick={() => setQuantity(quantity + 1)}>+</button>
        </div>

        <button onClick={handleAddToCart}>AÃ±adir al carrito</button>
        <button onClick={onClose}>Cerrar</button>
      </div>
    </div>
  );
};
