import React, { useState } from "react";
import useProductStore from "../Data/AsyncMock"; // Ruta a tu ProductStore
import ItemModal from "./ItemModal";
import { ClipLoader } from "react-spinners";

const Shop = () => {
  const { products, reduceStock } = useProductStore();
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState("Todos");

  const handleBuyClick = (product) => {
    setSelectedProduct(product);
  };

  const handleAddToCart = (product, quantity) => {
    reduceStock(product.id, quantity);
    setSelectedProduct(null); // Cierra el modal después de agregar
  };

  const filteredProducts =
    selectedCategory === "Todos"
      ? products
      : products.filter((product) => product.category === selectedCategory);

  return (
    <div className="max-w-[1240px] mx-auto px-4 py-8">
      <h1 className="text-4xl font-bold text-[#e6e8eb] text-center mb-8">Tienda</h1>

      <div className="text-center mb-8">
        {["Todos", "Minimalista", "Gamer", "Contemporáneo", "Moderno"].map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className="px-4 py-2 mx-2 bg-[#5f81a9] text-white rounded-md"
          >
            {category}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {filteredProducts.map((product) => (
          <div key={product.id} className="bg-[#131518] rounded-lg shadow-lg p-4">
            <img
              src={product.img}
              alt={product.name}
              className="h-40 w-full object-cover mb-4"
            />
            <h3 className="text-xl font-bold text-[#e6e8eb]">{product.name}</h3>
            <p className="text-[#aebbc9]">{product.price}</p>
            <p className="text-[#aebbc9]">Stock: {product.stock}</p>
            <button
              onClick={() => handleBuyClick(product)}
              className="w-full mt-4 px-4 py-2 bg-[#5f81a9] text-white rounded-md hover:bg-[#5f81a9]/80"
            >
              Comprar
            </button>
          </div>
        ))}
      </div>

      {selectedProduct && (
        <ItemModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={handleAddToCart} // Pasa la función como prop
        />
      )}
    </div>
  );
};

export default Shop;
